        /// <returns>Vector4 result of the shuffle operation.</returns>
        [MethodImpl(MethodImplOptions.AggressiveInlining)]
        public static Vector4 shuffle(Vector2 left, Vector2 right, ShuffleComponent x, ShuffleComponent y, ShuffleComponent z, ShuffleComponent w)
        {
            return Vector4(
                select_shuffle_component(left, right, x),
